export class MyPlaylist extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"})}connectedCallback(){this.shadowRoot.innerHTML='\n        <style>\n        </style>\n        <div id="playlistinterne">\n           <h3>Playlist</h3>\n            <ul>\n                <li id="./assets/audio/CleanGuitarRiff.mp3">Morceau 1</li>\n                <li id="morceau2.mp3">Morceau 2</li>\n                <li id="morceau3.mp3">Morceau 3</li>\n            </ul>\n        </div>\n        ',this.definitEcouteurs()}definitEcouteurs(){this.shadowRoot.querySelectorAll("#playlist li").forEach((e=>{e.onclick=e=>{let t=e.target.id;this.dispatchEvent(new CustomEvent("playsong",{detail:t,bubbles:!0,composed:!0}))}}))}}customElements.define("my-playlist",MyPlaylist);